    //         return error.message;
    //     } else {
    //         console.log('Unexpected error: ', error);
    //         return 'An unexpected error occurred';
    //     }
    // }
    return await axios({
        ...getConfig,
        url: `${getConfig.baseUrl}/${url}`,
        data
    }).then ( (response) => {
        console.log(response)
        return {
            status: response.status,
            data: response.data
        }
    }).catch((error) =>{
        console.log(error)
