import React from 'react'
import { Link } from 'react-router-dom';
import { useLoaderData } from 'react-router';
import axios from 'axios';

interface Brewery {
  id: string;
  name: string;
  brewery_type: string;
  address_1?: string | null;
  address_2?: string | null;
  address_3?: null;
  city: string;
  state_province: string;
  postal_code: string;
  country: string;
  longitude?: string | null;
  latitude?: string | null;
  phone?: string | null;
  website_url?: string | null;
  state: string;
  street?: string | null;
}

interface GetBreweriesResponse {
    data: Brewery[]
}


const Home = () => {
    const breweries = useLoaderData() as Brewery[];
  return (
    <div>
        <h2>Breweries list</h2>
        <p>Go to <Link to='/breweries'>Breweries</Link></p>
         <div>
            {breweries.map(brewery => (
                <p key={brewery.id}>{brewery.name}</p>
            ))}
        </div>
    </div>
  )
}

export default Home

export const breweriesLoader = async () => {
    // const response = await fetch('https://api.openbrewerydb.org/v1/breweries');
    // return response.json();
    try {
        const { data, status } = await axios.get<GetBreweriesResponse>('https://api.openbrewerydb.org/v1/breweries',
        {
            headers: {
                Accept: 'application/json',
            },
        });
        console.log(JSON.stringify(data, null, 4));
        console.log('response status: ', status);
        return data;
    } catch (error) {
        if (axios.isAxiosError(error)) {
            console.log('error message: ', error.message);
        } else {
            console.log('unexpected error:', error);
            return 'An unexpected error has occurred';
        }
    }
}
