import React from 'react'
import { useLoaderData, useParams } from 'react-router'
import axios from 'axios';

import Brewery from '../interfaces/Brewery';

interface GetBreweryDetailsResponse {
    data: Brewery[]
}

const BreweryDetails = () => {
    const { id } = useParams();
    const brewery = useLoaderData();

    return (
        <div className='brewery-details'>
            <h2>Details for {brewery.name}</h2>
     </div>
    )
}

export default BreweryDetails

//loader function
export const breweryDetailsLoader = async (params: Brewery) => {
    const { id } = params;
    try {
        const { data, status } = await axios.get<GetBreweryDetailsResponse>(`https://api.openbrewerydb.org/v1/breweries/{id}`, 
        {
            headers: {
                Accept: 'application/json',
            },
        });
        console.log('response status: ', status);
        return data;
    } catch (error) {
        if (axios.isAxiosError(error)) {
            console.log('error message: ', error.message);
        } else {
            console.log('unexpected error:', error);
            return 'An unexpected error has occurred';
        }
    }
}
